$( document ).ready(function() {
    $.ajax({
        url: "/svc/place/" + wlPlaceSlug + "/info",
        dataType: "json"
    }).done(function(response) {

        showNotification("info",response.text);
        $("#_wlPlaceLogo").attr("src","/images/ri5/default-place-logo.png");
        $("#_wlPlaceName").html(response.details.place.name);
        $("#_wlPlaceInfoLink").attr("href","/place/"+response.details.place.slug+"/info");
        $("#_wlPlaceAddressLink").attr("href","https://www.google.com/maps/place/?q=place_id:"+response.details.place.slug);
        //$("#wlPlaceAddress").html(item.place.addressdata.address_formatted);
        $("#_wlPlacePhone").html(response.details.place.phone);
    }).fail(function(jqXHR,textStatus, errorThrow){
        showNotification("error", JSON.parse(jqXHR.responseText).text + " | place : " + wlPlaceSlug);
    });
});